import React from 'react';
import axios from 'axios';

const baseUrl = "https://reqres.in/api/users?page=2";

class App extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            first_name: "NO info",
            last_name: "NO info",
        }

        this.recreateText = this.recreateText.bind(this);
    }


    render(){
        return(
            <div>
                <button onClick={this.recreateText}>Получить информацию о пользователях</button>
                <p>{this.state.first_name + " " + this.state.last_name}</p>
            </div>
        )
    }


    recreateText(){
        axios.get(baseUrl).then((res)=>{
            this.setState({first_name: res.data.data[1].first_name});
            this.setState({last_name: res.data.data[1].last_name});
            console.log(res.data.data);
        })
    }

}

export default App;
