import React from 'react';
import axios from 'axios';
import Card from './Card';
const baseUrl = "https://reqres.in/api/users?page=2";

class App extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            user : [{
                first_name: "NO info",
                last_name: "NO info",
                email: "NO info",
                avatar: "#####",
            }]
        }

        this.recreateText = this.recreateText.bind(this);
    }


    render(){
        return(
            <div>
                <button onClick={this.recreateText}>Получить информацию о пользователях</button>
                <Card first_name={this.state.user.first_name} last_name={this.state.user.last_name} email={this.state.user.email} avatar={this.state.user.avatar}/>
            </div>
        )
    }


    recreateText(){
        axios.get(baseUrl).then((res)=>{
            this.setState({first_name: res.data.data[1].first_name});
            this.setState({last_name: res.data.data[1].last_name});
            this.setState({email: res.data.data[1].email});
            this.setState({avatar: res.data.data[1].avatar});
            console.log(res.data.data.length);
        })
    }

}

export default App;
