import React from 'react';
import axios from 'axios';

const baseUrl = "https://reqres.in/api/users?page=2";

class App extends React.Component{

    constructor(props){
        super(props)
        this.state = {
            data: "NO info",
        }

        this.recreateText = this.recreateText.bind(this);
    }


    render(){
        return(
            <div>
                <button onClick={this.recreateText}>Click</button>
                <p>{this.state.data}</p>
            </div>
        )
    }


    recreateText(){
        axios.get(baseUrl).then((res)=>{
            this.setState({data: res.data.data[1].email});
        })
    }

}

export default App;
