import React from 'react';
import axios from 'axios';
import Card from './Card';

const baseUrl = "https://reqres.in/api/users?page=2";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [{
                first_name: "NO info",
                last_name: "NO info",
                email: "NO info",
                avatar: "#####",
            }]
        };

        this.recreateText = this.recreateText.bind(this);
    }

    render() {
        return (
            <div>
                <button onClick={this.recreateText}>Получить информацию о пользователях</button>
                <Card
                    first_name={this.state.users.length > 1 ? this.state.users[1].first_name : "NO info"}
                    last_name={this.state.users.length > 1 ? this.state.users[1].last_name : "NO info"}
                    email={this.state.users.length > 1 ? this.state.users[1].email : "NO info"}
                    avatar={this.state.users.length > 1 ? this.state.users[1].avatar : "#####"}
                />
                <Card
                    first_name={this.state.users.length > 1 ? this.state.users[2].first_name : "NO info"}
                    last_name={this.state.users.length > 1 ? this.state.users[2].last_name : "NO info"}
                    email={this.state.users.length > 1 ? this.state.users[2].email : "NO info"}
                    avatar={this.state.users.length > 1 ? this.state.users[2].avatar : "#####"}
                />
                <Card
                    first_name={this.state.users.length > 1 ? this.state.users[3].first_name : "NO info"}
                    last_name={this.state.users.length > 1 ? this.state.users[3].last_name : "NO info"}
                    email={this.state.users.length > 1 ? this.state.users[3].email : "NO info"}
                    avatar={this.state.users.length > 1 ? this.state.users[3].avatar : "#####"}
                />
            </div>
        );
    }

    recreateText() {
        axios.get(baseUrl).then((res) => {
            const newUsers = res.data.data.map((user) => ({
                first_name: user.first_name,
                last_name: user.last_name,
                email: user.email,
                avatar: user.avatar,
            }));
    
            this.setState({
                users: [...this.state.users, ...newUsers],
            }, () => {
                console.log(this.state.users);
            });
        });
    }
    
}

export default App;
